import React from 'react';
import { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import { ChatEngine } from 'react-chat-engine';
import { auth } from '../firebase';
import  { useAuth }  from '../context/AuthContexts';
const Chats = ()=>{
    const history = useHistory();
    const { user } = useAuth();
    const handleLogout = async () =>{
        await auth.signOut();
        history.push("/");
    }
    /*const [name, setName] = useState(null);
    let hehe;
    useEffect(auth.onAuthStateChanged((name) => {hehe = name}),[hehe]);
    console.log(hehe);
    */
   const [loading, setLoading] = useState(true);
    useEffect(() => {
        if(!user){
            history.push("/");
            return;
        }
        axios.get('https://api.chatengine.io/users/me',{
            headers: {
                "project-id" : "c12b0b1b-8a1f-4e45-be0e-ac7728a609bf",
                "user-name" : user.email,
                "user-secret" : user.uid,
            }
        })
        .then(() => {
            setLoading(false);
        })
        .catch(() => {
            let formdata = new FormData();
            formdata.append('email', user.email);
            formdata.append('username', user.displayName);
            formdata.append('secret', user.uid);
        })
    }, [user, history])
    return(
        <>
            <h1>HELLOâ¤ğŸ˜‚ğŸ˜‚ğŸ’–</h1>
            <h2>You have successfully logged in:</h2>
            <iframe style={{height : 96, width : 96}} src={user.photoURL}></iframe>
            <h4>Name : {user.displayName}</h4>
            <h4>Email : {user.email}</h4>
            <br></br>
            <br></br>
            <button onClick={handleLogout}>Logout</button>
            <ChatEngine
                projectID='c12b0b1b-8a1f-4e45-be0e-ac7728a609bf'
 			    userName={user.email}
 			    userSecret={user.uid}
            />
        </>
    )
};
export default Chats;
